contract Governable {
    // Storage position of the owner and pendingOwner of the contract
    // keccak256("XUSD.governor");
    bytes32 private constant governorPosition =
        0xfe7bb572d5723516ce4fe7a6a664f5adb38f5714efcd271411ed70a34d3ef2f7;

